import consumer from "./consumer"


  consumer.subscriptions.create({ channel: "NotificationsChannel"}, {
    connected() {
      console.log("NotificationsChannel connecte");
    },

    disconnected() {
      // Called when the subscription has been terminated by the server
    },
    received(data) {
      console.log(data);
          // Increase the notification count
          var notificationCount = $('#notification-count');
          var currentCount = parseInt(notificationCount.text());
          notificationCount.text(currentCount + 1);

          // Append a new dropdown item
          var notificationList = document.getElementById('notification-list');

        // Create a new list item
        var listItem = document.createElement('li');
        listItem.innerHTML = '<a class="dropdown-item" href="#">New notification</a>';

        // Append the new list item to the notification list
        notificationList.appendChild(listItem);
    }
  });
