      q: quantity,
      p: price,
      n: name,
    };
    carts[restid][id] = item;
    updateCart();
    increaseCartItemCount(); // Update cart item count
  });

  function updateCart() {
    localStorage.setItem("carts", JSON.stringify(carts));
    $("#cart-items").empty();
    var totalQuantity = 0;
    var totalPrice = 0;

    if (carts[restid]) {
      Object.keys(carts[restid]).forEach((item) => {
        var itemPrice = carts[restid][item].q * carts[restid][item].p;
        totalQuantity += carts[restid][item].q;
        totalPrice += itemPrice;

        var cartItemHtml =
          '<div class="cart-item">' +
          '<li class="list-group-item">Food Name: <strong>' +
          carts[restid][item].n +
          "</strong></li>" +
          '<li class="list-group-item">Quantity: ' +
          carts[restid][item].q +
          "</li>" +
          '<li class="list-group-item"> <strong> Price: Rs.' +
          itemPrice +
          "</strong></li>" +
          "</div>";

        $("#cart-items").append(cartItemHtml);
      });
    }

    $("#total-quantity").text(totalQuantity);
    $("#total-price").text("Rs" + totalPrice.toFixed(2));

    updateCartItemCount(); // Update cart item count after updating cart items
  }

  // Cart Checkout button click event
  $("#checkout").click(function () {

    localStorage.setItem("carts", JSON.stringify(carts)); // Update the carts object in local storage
    sessionStorage.removeItem("cart");

    $.ajax({
      url: "/resturants/" + restid + "/orders",
      method: "POST",
      data: {
        cart_items: carts[restid],
        authenticity_token: $('meta[name="csrf-token"]').attr("content"),
      },
      success: function (response) {
        alert("Added to cart successfully!");
        updateCartItemCount(); // Update cart item count after successful order
      },
      error: function (xhr, status, error) {
        alert("Error placing the order. Please try again.");
      },
    });
    delete carts[restid]; // Remove the cart for the current restaurant
  });
