import consumer from "./consumer"


consumer.subscriptions.create("NotificationChannel", {
  connected() {
    // Called when the subscription is ready for use on the server
    console.log("NotificationsChannel")
  },

  disconnected() {
    // Called when the subscription has been terminated by the server
  },
  received(data) {
    // Update the notification count on the bell icon
    const notificationCount = document.getElementById('notification-count');
    const currentCount = parseInt(notificationCount.innerText);
    notificationCount.innerText = currentCount + 1;

    // Display the notification message
    // You can customize how the notification is displayed (e.g., using a toast library)
    console.log(data.message);
  }
});
