import consumer from "./consumer"


  consumer.subscriptions.create('NotificationsChannel', {
    connected() {
      console.log("NotificationsChannel connected");
    },

    disconnected() {
      // Called when the subscription has been terminated by the server
    },

    received(data) {
      console.log(data);
      increaseNotificationCount();
    }
  });

  function increaseNotificationCount() {
    const notificationCountElement = document.getElementById('notification-count');
    const currentCount = parseInt(notificationCountElement.innerText);
    console.log(currentCount);
    const newCount = currentCount + 1;
    notificationCountElement.innerText = newCount.toString();
    console.log(notificationCountElement.innerText);
    notificationCountElement.setAttribute('data-count', newCount);
  }
