import consumer from "./consumer"

$(document).on('DOMContentLoaded', function () {
  const notificationList = $('#notification-list');
  const notificationCountElement = $('#notification-count');
  const resetCountButton = $('#reset-count-button');

  consumer.subscriptions.create('NotificationsChannel', {
    connected() {
      console.log("NotificationsChannel connected");
    },

    disconnected() {
      // Called when the subscription has been terminated by the server
    },

    received(data) {
      const newNotificationItem = $('<li>').append($('<a>').attr('href', '#').text(data.message));
      notificationList.append(newNotificationItem);
      increaseNotificationCount();
    }
  });

  resetCountButton.on('click', function () {
    resetNotificationCount();
  });

  function increaseNotificationCount() {
    const currentCount = parseInt(notificationCountElement.text());
    const newCount = currentCount + 1;
    notificationCountElement.text(newCount);
    console.log(notificationCountElement.text(newCount));
    notificationCountElement.attr('data-count', newCount);
  }

  function resetNotificationCount() {
    notificationCountElement.text('0');
    notificationCountElement.attr('data-count', '0');
  }
});
