import consumer from "./consumer"

document.addEventListener('turbolinks:load', () => {
  const user_id = document.getElementById('notification-count').getAttribute('data-user-id');
  console.log(user_id);
  consumer.subscriptions.create({ channel: "NotificationsChannel", id: user_id }, {
    connected() {
      console.log("NotificationsChannel connecte"+user_id);
    },

    disconnected() {
      // Called when the subscription has been terminated by the server
    },
    received(data) {
      console.log("NotificationsChannel data");
      // const notificationCount = document.getElementById('notification-count');
      // const currentCount = parseInt(notificationCount.innerText);
      // notificationCount.innerText = currentCount + 1;

      console.log(data.message);
    }
  });
});
