import consumer from "./consumer"


  consumer.subscriptions.create({ channel: "NotificationsChannel"}, {
    connected() {
      console.log("NotificationsChannel connecte");
    },

    disconnected() {
      // Called when the subscription has been terminated by the server
    },
    received(data) {
      const notificationCount = document.getElementById('notification-count');
      const currentCount = parseInt(notificationCount.innerText); 
      console.log(currentCount);   
      notificationCount.innerText = currentCount + 1;
      console.log(data.message);
      const message = data.message;
      console.log(message);
      var listItem = $('<li>' + message + '</li>');
      $('#notification-list').append(listItem);
    }
  });
